mapper.select:
select 
  	a.Goodsname, a.Quantity, a.Consignment, a.ReceivingPlace, a.bidTime, IF(a.bidStart=1,'已标选','未标选') as bidStart, a.finishStart, 
    a.Time,u.username
  	from lgbid a left join lguser u on a.userid = u.userid
  	where 1=1
  	
  		and a.time >= #{startTime}
  	
  	
  		and a.time <= #{endTime}
  	
  	
  		and u.username = #{userName}
  	
  	order by a.time desc
